import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Register from './Resources/register';
import FirstPage from './Resources/starting';
import Account from './Resources/account';
import './App.css';




class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      authed : false,
      sessionId: ''
    }
  }
  isAuthenticated(){
    fetch('/login', {
      method: 'POST',
      credentials: 'include', // This is important to enable cookies
      // Include any necessary data like username and password
      // ...
    })
    .then(response => {
      response.setState({authed: true});
    })
    .catch(error => {
      console.log(error);
    });
    fetch('/api/session', {
      method: 'GET',
      credentials: 'include',
    })
    .then(response => response.json())
    .then(data => {
      const sessionId = data.sessionId; // Access session data
      this.setState({authed: true, sessionId: sessionId})
    })
    .catch(error => {
      console.log(error)
    });

  }
  render() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<FirstPage />} />
        <Route path="/register" element={<Register />} />
        <Route path="/account" element={this.state.authed ? <Account /> : <FirstPage />} />
      </Routes>
    </Router>
  );
}
}

export default App;
