import React, { Component } from 'react';
import { NavLink } from 'react-router-dom'; 
import Logo from '../Photo/logo.png';
import './css/headerAcc.css'
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { setDivVisibility } from '../action/action';
import { connect } from 'react-redux';
import rootReducer from '../reducer/reducer';

class HeaderAcc extends Component {
  componentDidMount() {
    this.setupMediaQueryListener();
  }

  componentWillUnmount() {
    this.removeMediaQueryListener();
  }

  setupMediaQueryListener() {
    const mediaQuery = window.matchMedia('(max-width: 1000px)');
    this.handleMediaChange(mediaQuery);
    mediaQuery.addEventListener('change', this.handleMediaChange); // Add 'change' event
  }
  
  removeMediaQueryListener() {
    const mediaQuery = window.matchMedia('(max-width: 1000px)');
    mediaQuery.removeEventListener('change', this.handleMediaChange); // Add 'change' event
  }
  

   handleButtonClick = () => {
    // Toggle the visibility of the div using Redux state
    this.props.setDivVisibility(!this.props.isDivVisible);
  };
  render() {
    return (
      <div className="headerAcc">
        <div className="child">
          <div className="headerAccLogoH">
            <NavLink to='/account'><img src={Logo} alt="TSU Logo" /></NavLink>
            <div className="headerAccHashes">
              <h1>თბილისის სახელმწიფო უნივერსიტეტი</h1>
              <h1>Tbilisi State University</h1>
            </div>
          </div>
          <input type="search" placeholder="Search Person" />
          <div className="NavLinks-NavBar">
            <NavLink to=''><span className="material-symbols-outlined">chat</span></NavLink>
            <NavLink to=''><span className="material-symbols-outlined">notifications</span></NavLink>
            <NavLink to=''><span className="material-symbols-outlined">account_circle</span></NavLink>
            <NavLink to='/' id="logout"><span className="material-symbols-outlined">logout</span></NavLink>
            <button onClick={this.handleMediaChange} id='menu'><span className="material-symbols-outlined">menu</span></button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  isDivVisible: state.isDivVisible,
});

const mapDispatchToProps = {
  setDivVisibility,
};

export default connect(mapStateToProps, mapDispatchToProps)(HeaderAcc);
