import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Register from './Resources/register';
import FirstPage from './Resources/starting';
import Account from './Resources/account';
import './App.css';
import { useEffect } from 'react';

// Custom hook to check authentication



function App() {function isAuthenticated() {
  const [auth, setAuth] = useState(false);

  useEffect(() => {
    // Check if a session ID exists in your session storage
    const sessionID = sessionStorage.getItem('session-id-key');

    // Update the auth state based on whether the session ID exists
    setAuth(!!sessionID);
  }, []);

  return auth;
}

  return (
    <Router>
      <Routes>
        <Route path="/" element={<FirstPage />} />
        <Route path="/register" element={<Register />} />
        <Route path="/account" element={isAuthenticated ? <Account /> : <FirstPage />} />
      </Routes>
    </Router>
  );
}

export default App;
