import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Register from './Resources/register';
import FirstPage from './Resources/starting';
import Account from './Resources/account';
import LogIn from './Resources/Index/LogIn';
import './App.css';
import { setState } from 'react';




class App extends Component {
  constructor(props){
    super(props);this.state = {
      authed: false,
    };
  }

  // Function to update authed state
  setAuthed = (value) => {
    this.setState({ authed: value });
  }

  render() {

  const authed = useSelector((state) => state.auth.authed);
  return (
    <Router>
      <Routes>
        <Route path="/" element={<FirstPage/>} />
        <Route path="/register" element={<Register />} /><Route
            path="/login"
            element={<LogIn authed={this.state.authed} setAuthed={this.setAuthed} />}
          />
        <Route path="/account" element={this.props.authed ? <Account /> : <FirstPage />} />
      </Routes>
    </Router>
  );
}
}

export default App;
