import React, { Component } from "react";
import HeaderOne from "./header";
import './css/register.css';
import Tsu from "./Photo/regImg.jpg"
import { NavLink } from "react-router-dom";
import axios from "axios";
import withRouter from 'react-router-dom'

class Register extends Component {
  test(event){
    this.props.history.push('/account')
  }
    constructor(props) {
        super(props);
        this.state = {
            value: '',
            password: '',
            confirmPassword: '',
            error: '',
            errorTwo: '',
            name: '',
            lastName: '',
            email: '',
            personalN: '',
            registrationMessage: '',
            redirectToAccount: true,
        }
    }
    handleInputChange = (e) => {
        // Get the input value
        let value = e.target.value;
    
        // Remove any non-numeric characters using a regular expression
        value = value.replace(/[^0-9]/g, '');
        if (value.length > 11) {
            value = value.slice(0, 11);
          }
    
        // Update the state with the cleaned value
        this.setState({ value });
      }

      handleName = (e) => {
        const name = e.target.value;
        this.setState({ name: name });
      }
      
      handleLastName = (e) => {
        const lastName = e.target.value;
        this.setState({ lastName: lastName });
      }
      
      handleMail = (e) => {
        const email = e.target.value;
        this.setState({ email: email });
      }
      

      handlePassword = (e) => {
        let password = e.target.value;
        password = password.replace(/[,;*$]/g, '');
        if (/^[a-zA-Z0-9!@#$%^&(){}<>]*$/.test(password)) {
            this.setState({
              password,
              error: '',
            });
          }
        else if (/[,%;*]/.test(password)) {
            this.setState({ 
                password, 
                error: 'Password should not contain , % ; and * characters!'
            });
        } 
      }

      confirmPassword = (e) => {
        let confirmPassword = e.target.value;

        if (confirmPassword === this.state.password) {
            this.setState({
                confirmPassword,
                errorTwo: '' 
            });
        } else{
            this.setState({
                confirmPassword,
                errorTwo: 'Password does not match'
            })
        }
      };
      handleRegistration = async (e) => {
        const { name, lastName, password,  email, value } = this.state;
        const API_URL = 'http://localhost:3002'; // Replace with your backend URL

      
        try {
          const response = await fetch(API_URL, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              name,
              lastName,
              password,
              email,
              personalN: value,
              studentOrLecturer: 'student',
            }),
          });
      
          if (response.ok) {
            // Registration was successful, now check if the user already exists
            const checkUserResponse = await fetch(API_URL, {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({
                personalN: value,
                email,
              }),
            });
      
            if (checkUserResponse.ok) {
              // User does not exist, navigate to the account page
              const data = await response.json();
              alert(data)
              this.setState({redirectToAccount: true});
              this.test(e)
            } else {
              // User already exists, handle accordingly (e.g., show an error message)
              alert('User with this email or personalN already exists');
            }
          } else {
            // Registration failed, handle accordingly (e.g., show an error message)
            throw new Error('Registration failed');
          }
        } catch (error) {
          console.error('Error registering:', error);
        }
      };
      
    render() {
    return(
        <div>
            <HeaderOne className="regHead"/>
            <div className="main-register">
                <div className="registration-form">
                    <h2>Sign Up</h2>
                    <form onSubmit={this.handleRegistration}>
                        <div className="form-row">
                            <div>
                                <label htmlFor="Name">First Name</label>
                                <input type="text" name="name"
                                value={this.state.name} 
                                onChange={this.handleName}
                                required/>
                            </div>
                            <div>
                                <label htmlFor="LastName">Last Name</label>
                                <input type="text" name="lastName"  
                                value={this.state.lastName}
                                onChange={this.handleLastName}
                                required/>
                            </div>
                        </div>
                        <div className="form-row">
                            <div>
                                <label htmlFor="password">Password</label>
                                <input type="password" name="password" 
                                value={this.state.password}
                                onChange={this.handlePassword}
                                required/>
                                {this.state.error && <p style={{ color: 'red' }}>{this.state.error}</p>}
                            </div>
                            <div>
                                <label htmlFor="repeatPassword">Repeat Password</label>
                                <input type="password" name="repeatPassword" 
                                value={this.state.confirmPassword}
                                onChange={this.confirmPassword}
                                required />
                                
                                {this.state.errorTwo && <p style={{ color: 'red' }}>{this.state.errorTwo}</p>}
                            </div>
                        </div>
                        <div className="form-row">
                            <div>
                                <label htmlFor="Email">Email</label>
                                <input type="email" name="email"
                                value={this.state.email} 
                                onChange={this.handleMail}required />
                            </div>
                            <div>
                                <label htmlFor="PersonalN">Personal Number</label>
                                <input 
                                    type="type" 
                                    name="PersonalN" 
                                    required 
                                    value={this.state.value}
                                    onChange={this.handleInputChange}
                                />
                            </div>
                        </div>
                        <div className="form-row">
                            <div>
                                <label htmlFor="studentOrLecturer">I am ...</label>
                                <select name="studentOrLecturer" id="studentOrLecturer" required>
                                    <option value="student">Student</option>
                                </select>
                            </div>
                        </div>
                        <div className="button">
                            <input type="submit" name="signUp" value="SignUp" id="signUp" onClick={()=>{
                              const navigate = useNavigate();
                              if(this.props.redirectToAccount){
                                navigate('/account')
                              }
                              else{
                                navigate('/account')
                              }
                            }} />
                        </div>
                        <p>{this.state.registrationMessage}</p>
                    </form>
                    <p>Or if you already have an account</p>
                    <NavLink to="/">Log In</NavLink>
                </div>
                <img src={Tsu} alt="Tbilisi State University"></img>
            </div>
        </div>
    )
}
}

export default withRouter(Register)
