import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Register from './Resources/register';
import FirstPage from './Resources/starting';
import Account from './Resources/account';
import './App.css';

// Your authentication logic (e.g., a function that checks if the user is authenticated)
function isAuthenticated() {
  // Check if a session ID exists in your session storage
  // Replace 'session-id-key' with the actual key you use to store the session ID.
  const sessionID = sessionStorage.getItem('session-id-key');

  // Return true if the session ID exists (user is authenticated), false otherwise.
  return !!sessionID;
}


function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<FirstPage />} />
        <Route path="/register" element={<Register />} />
        <Route path="/account" element={isAuthenticated ? <Account /> : <FirstPage />} />
      </Routes>
    </Router>
  );
}

export default App;
