import React from "react";
import HeaderAcc from "./account/headerAcc";
import LeftNavBar from "./account/leftNavBar";
import "./css/account.css"
import { Component } from "react";
import MessengerTab from "./account/messengerTab";
import Feed from "./account/feed";
import Register from "./register";
import FirstPage from "./starting";

export default class Acc extends Component{
    constructor(props){
        super(props);
        this.state = {
            authed : false
        }
    }
    async isAuthed() {
        const API_URL = 'localhost:3002/account';
        try {
            const response = await fetch(API_URL, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: "include",
                })

            if(!response.ok){
                this.setState({authed: false})
            }
            this.setState({authed: true})
        }
        catch (err) {
            console.log(err);
            alert(err.message);
        }
    }
    render(){
    return(
        <div>
            <h2 id="welcome">{this.props.registrationMessage}</h2>
            <HeaderAcc 
            className="headerAccCss" 
            />
            <LeftNavBar 
            className="leftNavBar"
    />
            <MessengerTab 
            className="messengerTabAcc"
    />
            <Feed />
        </div>
    )
    }
}
