import { Component } from "react";
import React from "react";
import './css/feed.css'
import { useState, setState } from "react";
import proIcon from "../Photo/proIcon.png";

export default class Feed extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedFileImg: [],
            name: '',
            lastName: '',
            userPhotoUrl: proIcon,
        }
    }
    async isAuthed() {
        const API_URL = 'http://localhost:3002/account'; // Include the protocol
        try {
        const response = await fetch(API_URL, {
            method: 'GET',
            headers: {
            'Content-Type': 'application/json',
            },
            credentials: 'include',
        });
        
        if (response.ok) {
            const data = await response.json();
            this.setState({ name: data.name, lastName: data.lastName });
        }
        } catch (err) {
        console.log(err);
        alert(err.message);
        }
    }
    async componentDidMount() {
        // Check authentication status when the component mounts
        await this.isAuthed();
    }
  
    
    handleImg = (e) => {
        const selectedFileImg = Array.from(e.target.files);
    this.setState((prevState) => ({
      selectedFileImg: [...prevState.selectedFileImg, ...selectedFileImg],
    }));
    }
    removeImage = (index) => {
        const { selectedFileImg } = this.state;
        const updatedFiles = [...selectedFileImg];
        updatedFiles.splice(index, 1);
        this.setState({ selectedFileImg: updatedFiles });
      };componentDidMount() {
        // Fetch the user's photo from the server when the component mounts
        this.getUserPhoto();
        this.getUserInfo()
      }
    
      getUserPhoto = async () => {
        try {
          // Fetch the user's photo from the server using the /getPhoto endpoint
          const response = await fetch('http://localhost:3002/uploaded', {
            method: 'GET',
            credentials: 'include',
            headers: {
              Accept: 'image/jpeg', // Adjust the content type as needed
            },
          });
    
          if (response.ok) {
            // Create a blob URL from the response data to display the image
            const blob = await response.blob();
            const userPhotoUrl = URL.createObjectURL(blob);
    
            // Update the state with the user's photo URL
            this.setState({ userPhotoUrl });
          } else {
            console.error('Error fetching user photo');
          }
        } catch (error) {
          console.error(error);
        }
      };
        async componentDidMount() {
          // Fetch user info when the component mounts
          await this.getUserInfo();
        }
      
        async getUserInfo() {
          try {
            const response = await fetch('http://localhost:3002/profile', {
              method: 'GET',
              credentials: 'include',
            });
      
            if (response.ok) {
              const data = await response.json();
              this.setState({
                name: data.name,
                lastName: data.lastName,
                email: data.email,
                personalN: data.personaln,
              });
            } else {
              console.error('Error fetching user photo');
            }
          } catch (error) {
            console.error('Error fetching user photo', error);
          }
        }
      
        handleImg = (e) => {
          const selectedFiles = Array.from(e.target.files);
          this.setState((prevState) => ({
            selectedFileImg: [...prevState.selectedFileImg, ...selectedFiles],
          }));
        };
      
        removeImage = (index) => {
          const { selectedFileImg } = this.state;
          const updatedFiles = [...selectedFileImg];
          updatedFiles.splice(index, 1);
          this.setState({ selectedFileImg: updatedFiles });
        };
      
        addPost = async (e) => {
          e.preventDefault();
          const API = 'http://localhost:3002/post';
          const formData = new FormData();
          this.state.selectedFileImg.forEach((file, index) => {
            formData.append(`postPhoto${index}`, file);
          });
          try {
            const editorContent = document.getElementById('editor').textContent;
            // Create an object to send to the server
            const data = { content: editorContent };
            const response = await fetch(API, {
              method: 'POST',
              credentials: 'include',
              body: formData,
            });
            if (response.ok) {
              alert('Post successfully added');
            } else {
              alert('Error adding post');
            }
          } catch (err) {
            console.error('Error adding post', err);
            alert('Error adding post');
          }
        };
      
        render() {
          return (
            <div className="feed">
              <div className="feed-container">
                <button className="feedProfileBut">
                  <div className="feedPro">
                    <img id="profileImgOnFeed" src={this.state.userPhotoUrl} alt="User" />
                    <p>{`${this.state.name} ${this.state.lastName}`} to anyone</p>
                  </div>
                </button>
                <form onSubmit={this.addPost}>
                  <div className="editor" id="editor" contentEditable="true" role="textbox" aria-multiline="true" data-placeholder="Write anything you want" aria-label="Write Post"></div>
                  <div className="postTools">
                    <div className="photo">
                      <input
                        type="file"
                        name="postPhoto"
                        id="uploadPhotoForPost"
                        accept=".png, .jpg, .webp"
                        multiple
                        onChange={this.handleImg}
                      />
                      <label htmlFor="uploadPhotoForPost" className="fileLabel">Upload Photo</label>
                      {this.state.selectedFileImg.length > 0 && (
                        <div className="selectedImg">
                          <p>Selected Images:</p>
                          <ul>
                            {this.state.selectedFileImg.map((file, index) => (
                              <li key={index}>
                                <img
                                  src={URL.createObjectURL(file)}
                                  alt={`Selected ${index}`}
                                  width="100"
                                  height="100"
                                />
                                <button id="removeSelectedImg" onClick={() => this.removeImage(index)}>X</button>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                    <div className="postButton">
                      <input type="submit" value="Post" id="postButton" />
                    </div>
                  </div>
                </form>
              </div>
            </div>
          );
    }
}
