import React, {Component} from 'react';
import HeaderAcc from './account/headerAcc'
import LeftNavBar from './account/leftNavBar'
import MessengerTab from './account/messengerTab';
import './css/profile.css'
import proIcon from './Photo/proIcon.png';

class Profile extends Component{
    constructor(props) {
        super(props);
        this.state = {
          selectedFile: '',
        };
        this.submitPhoto = this.submitPhoto.bind(this);
      }
    
      handleFileChange = (e) => {
        const file = e.target.files[0];
        this.setState({ selectedFile: file });
      };

      submitPhoto(e){
        e.preventDefault();
        const  selectedFile  = this.state.selectedFile;
        const API = 'http://localhost:3002/upload';

        try {
            const response = fetch(API, {
                method: 'POST',
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    selectedPhoto: selectedFile,
                })
            });
            if(!response.ok) {
                alert('Error');
            }
            else{
                alert('Success');
            }
        }
        catch (err) {
            console.log(err);
            alert(err.message);
        }
      }
    render() {
        return (
            <>
            <HeaderAcc />
            <LeftNavBar />
            <MessengerTab />
            <div>
                <div className='profileInfo'>
                    <div className='photoManage'>
                    <form onSubmit={this.submitPhoto}>
                        <img id="profileImg" src={this.state.selectedFile ? URL.createObjectURL(this.state.selectedFile): proIcon} /> 
                        <button type="submit">Upload File</button>
                    </form>
                        <input type="file" accept="image/*" onChange={this.handleFileChange} />
                    </div>
                    <h1>Photo</h1>
                </div>
            </div>
            </>
        )
    }
}

export default Profile
