import React, { Component } from "react";
import './css/messengerTab.css';
import { useState, useEffect } from "react";

export default class MessengerTab extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hasMessages: true,
        };
    }

    componentDidMount() {
        // Check for buttons with the class "message" after the component is mounted
        const buttons = document.querySelectorAll('.message');
        if (buttons.length > 0) {
            this.setState({ hasMessages: false });
        }
    }
    render() {
        return (
            <div className="messengerTab">
                <button className="messengerTabButton" onClick=''>Messages <span className="material-symbols-outlined">chat</span></button>
                <div className="messengerTabMessages" ref={this.messageRef}>
                {this.state.hasMessages ? <h3>You have no message</h3> :<h3>You have {this.buttons} messages</h3>}
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                </div>
            </div>
        )
    }
}
