import React, { Component } from "react";
import './css/messengerTab.css';
import { useState, useEffect } from "react";

export default class MessengerTab extends Component {
    constructor(props){
        super(props);
        this.state = {
            isHVisible: false
    }
    checkForMessages = () => {
        // Check if there are any buttons with the class "message"
        const buttons = document.querySelectorAll('.message');
        if(buttons.length === 0){
            this.setState.isHVisible = true
        }
        else{
            this.setState.isHVisible = false
        }
    };
}
    render() {
        return (
            <div className="messengerTab">
                <button className="messengerTabButton" onClick=''>Messages <span className="material-symbols-outlined">chat</span></button>
                <div className="messengerTabMessages">
                    {this.checkForMessages ? (
                    <h3>You have no message</h3>
                ) : null}
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                    <button className="message"> someone</button>
                </div>
            </div>
        )
    }
}
