import React, {Component} from 'react';
import HeaderAcc from './account/headerAcc'
import LeftNavBar from './account/leftNavBar'
import MessengerTab from './account/messengerTab';
import './css/profile.css'
import proIcon from './Photo/proIcon.png';

class Profile extends Component{
    constructor(props) {
        super(props);
        this.state = {
          selectedFile: '',
          userPhoto: proIcon,
        };
        this.submitPhoto = this.submitPhoto.bind(this);
      }
    
      handleFileChange = (e) => {
        const file = e.target.files[0];
        this.setState({ selectedFile: file });
      };

      submitPhoto= async (e) => {
        e.preventDefault();
        const API = 'http://localhost:3002/upload';
        const formData = new FormData();
        formData.append('photo', this.state.selectedFile); 

        try {
            const response =await fetch(API, {
                method: 'POST',
                credentials: 'include',
                body:  formData,
            });
            if(!response.ok) {
                alert('Error');
            }
            else{
                alert('Success');
            }
        }
        catch (err) {
            console.log(err);
            alert(err.message);
        }
      }
      userPhotoes = async () => {
        const API = 'http://localhost:3002/upload';
        
        try{
        const userPhotoResponse = await fetch(API, {
            method: 'GET',
            credentials: 'include',
        })
        if (!userPhotoResponse.ok) {
            alert('Error')
        }
        const userPhoto = await userPhotoResponse.json();
        this.setState({userPhoto: userPhoto})
    }
    catch (err) {
        console.error(err)
        alert(err.message)
    }
      }
    render() {
        return (
            <>
            <HeaderAcc />
            <LeftNavBar />
            <MessengerTab />
            <div>
                <div className='profileInfo'>
                    <div className='photoManage'>
                        <img id="profileImg" src={this.state.selectedFile ? URL.createObjectURL(this.state.selectedFile): URL.createObjectURL(this.state.userPhoto)} /> 
                    <form onSubmit={this.submitPhoto} encType="multipart/form-data">
                        <input type="file" accept="image/*" name="photo" onChange={this.handleFileChange} />
                        <button type="submit">Upload File</button>
                    </form>
                    </div>
                    <h1>Photo</h1>
                </div>
            </div>
            </>
        )
    }
}

export default Profile
