import React, { Component } from "react";
import './login.css';
import { NavLink } from "react-router-dom";

class LogIn extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: '',
            passwordLogIn: '',
            error: '',
            showPassword: false
        }
    }
    handleLogin = (e) => {
        let value = e.target.value;
        value = value.replace(/[^0-9]/g, '');
        if (value.length > 11) {
            value = value.slice(0, 11)
        };

        this.setState({ value: value});
    };
    handlePasswordLogIn = (e) => {
        let password = e.target.value;
        password = password.replace(/[,;*$]/g, '');
        if (/^[a-zA-Z0-9!@#$%^&(){}<>]*$/.test(password)) {
            this.setState({
              password,
              error: '',
            });
          }
        else if (/[,%;*]/.test(password)) {
            this.setState({ 
                password, 
                error: 'Password should not contain , % ; and * characters!'
            });
        } 
      }
      
    passwordVisibility = () => {
        this.setState((prevState) => ({
          showPassword: !prevState.showPassword,
        }));
      };
      handleLogin= async (e) => {
        e.preventDefault();
        const {passwordLogIn, value} = this.state;
        const API_URL = 'http://localhost:3002/login'; 

        try{
            const loginResponse = await fetch(API_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    value,
                    passwordLogIn
                })
            });
            if(!loginResponse.ok) {
                alert('User not logged in')
            }
            else{
                window.location.href = '/account'
            }
        }
        catch(err) {
            alert('Login failed: ' + err.message);
            console.log(err);
        }
      }
    render() {
        const {  showPassword } = this.state;
    const inputType = showPassword ? 'text' : 'password';
    return(
        <div className="form-login-div">
            <h2>Authorization</h2>
            <form className="form-login">
                <label htmlFor = "Personal N">Personal N</label>
                <input type="text" name="PersonalN" 
                    value={this.state.value}
                    onChange={this.handleLogin}
                    required
                />
                <label htmlFor = "Password" >Password</label>
                <div className="password">
                <input type={inputType} name="Password" 
                    value={this.state.passwordLogIn}
                    onChange={this.handlePasswordLogIn} 
                    required/>
                    <button type="button" onClick={this.passwordVisibility}>
                      {showPassword ? <span class="material-symbols-outlined">visibility_off</span> : <span class="material-symbols-outlined">visibility</span>}
                    </button>
                </div>
                <div className="checkbox">
                    <input type="checkbox" name="rememberMe" value="Remember Me" />
                    <label htmlFor="rememberMe">Remember Me</label>
                </div>
                <input type="submit" name="LogIn" value="LogIn" id="logIn" />
            </form>
            <NavLink to="/account">acc</NavLink>
        </div>
    )
}
}

export default LogIn
