import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Register from './Resources/register';
import FirstPage from './Resources/starting';
import Account from './Resources/account';
import './App.css';




class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      authed : false,
    }
  }componentDidMount() {
    // Check if the user is authenticated when the component mounts
    this.checkAuthentication();
  }

  checkAuthentication() {
    // Make a request to an endpoint on your server that checks if the user is authenticated.
    // This should typically be a GET request.
    fetch('/login', {
      method: 'POST',
      credentials: 'include',
    })
      .then((response) => {
        if (response.status === 200) {
          this.setState({ authed: true });
        }
      })
      .catch((error) => {
        console.log(error);
      });

  }
  render() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<FirstPage />} />
        <Route path="/register" element={<Register />} />
        <Route path="/account" element={this.state.authed ? <Account /> : <FirstPage />} />
      </Routes>
    </Router>
  );
}
}

export default App;
