// reducers/authReducer.js
import {login, logout} from '../action/action'

const initialState = {
    isAuthenticated: false,
  };
  
  const authReducer = (state = initialState, action) => {
    switch (action) {
      case 'LOGIN':
        return {
          ...state,
          authed: true,
        };
      case 'LOGOUT':
        return {
          ...state,
          authed: false,
        };
      default:
        return state;
    }
  };
  
  export default authReducer;
