import React, { Component } from 'react';
import { NavLink } from 'react-router-dom'; // Import NavLink
import Logo from '../Photo/logo.png'; // 
import LeftNavBar from './leftNavBar';
import './css/headerAcc.css'

class HeaderAcc extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: false,
    };
  }

  toggleVisible = () => {
    this.setState((prev) => ({ isVisible: !prev.isVisible }));
  };
  
  componentDidMount() {
    // Check the initial screen width and set isVisible accordingly
    this.handleScreenWidthChange();
    window.addEventListener('resize', this.handleScreenWidthChange);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleScreenWidthChange);
  }

  handleScreenWidthChange = () => {
    // Check the screen width and set isVisible based on the range
    const screenWidth = window.innerWidth;
    const isVisible = screenWidth >= 200 && screenWidth <= 1000;
    this.setState({ isVisible });
  };

  render() {
    return (
      <div className="headerAcc">
        <div className="child">
          <div className="headerAccLogoH">
            <NavLink to='/account'><img src={Logo} alt="TSU Logo" /></NavLink>
            <div className="headerAccHashes">
              <h1>თბილისის სახელმწიფო უნივერსიტეტი</h1>
              <h1>Tbilisi State University</h1>
            </div>
          </div>
          <input type="search" placeholder="Search Person" />
          <div className="NavLinks-NavBar">
            <NavLink to=''><span className="material-symbols-outlined">chat</span></NavLink>
            <NavLink to=''><span className="material-symbols-outlined">notifications</span></NavLink>
            <NavLink to=''><span className="material-symbols-outlined">account_circle</span></NavLink>
            <NavLink to='/' id="logout"><span className="material-symbols-outlined">logout</span></NavLink>
            <button onClick={this.toggleVisible} id="menu"><span className="material-symbols-outlined">menu</span></button>
          </div>
        </div>
        <LeftNavBar isVisible={this.state.isVisible} /> {/* Pass the isVisible state as a prop */}
      </div>
    );
  }
}

export default HeaderAcc;
