import React from "react";
import Account from './account'
import FirstPage from "./starting";
import { useState } from "react";

function AccountGuard() {
  const [isAuthenticated, setIsAuthenticated] = useState(null);

  useEffect(() => {
    const API_URL = 'http://localhost:3002/account'; 

    fetch(API_URL, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
    .then(response => response.json())
    .then(data => {
      // Assuming your server sends back an authentication status
      setIsAuthenticated(data.isAuthenticated);
    })
    .catch(error => {
      console.error('Error checking authentication:', error);
      setIsAuthenticated(false); // Set to false on error
    });
  }, []);

  if (isAuthenticated === null) {
    // Waiting for authentication status
    return null;
  } else if (isAuthenticated) {
    // User is authenticated, render the protected route
    return <Account />;
  } else {
    // User is not authenticated, redirect to the login page
    return window.location.href = '/login';
  }
}

export default AccountGuard;
